import React, { useState } from "react";
import PopupDetail from './PopupDetail';
import './VideoOutput.css'


function VideoOutput(probs) {
    const [popupOpen, setPopupOpen] = useState(false);

    let popup = null;
    if(popupOpen){
        if(!probs.videoOutFile){
            console.log("Please select image");
            setPopupOpen(false)
            return;
        }
       popup = <PopupDetail imgOutUrl={probs.vidOutUrl} detailOut={probs.detailOut} onPopupClose={() => setPopupOpen(false)} />
    }

    let vidOutput = null;
    if(probs.vidOutUrl !== ' '){
        vidOutput = vidInput = <video controls src={probs.vidInUrl} alt="VideoIn"> Sorry, your browser doesn't support embedded videos.</video>
    }
  return (
    <div className="Video-content-output">
        <div>
            <p className="Video-content-output-video">
                {vidOutput}
            </p>
            <p>
                <button className="Video-content-output-button" onClick={() => setPopupOpen(true)}>Detail</button>
            </p>
        </div>
        <div>
            {popup}
        </div>
    </div>
  )
}

export default VideoOutput
