import React, { useRef, useState } from "react";
import axios from 'axios';
import DetectionOutput from "./DetectionOutput";
import './DetectionPage.css';
import VideoInput from "./VideoInput";

function VideoPage() {
    const videoRef =useRef(null);
    const [videoInUrl, setVideoInUrl] =  useState(' ');
    const [videoInFile, setVideoInFile] =  useState(null);
    const [videoOutUrl, setVideoOutUrl] =  useState(' ');
    const [videoOutFile, setVideoOutFile] =  useState(null);
    const [detailOut, setDetailOut] =  useState(null);

    function onUploadfileClick(){
        const myFileVideoIn = videoRef.current.files[0];
        setVideoOutFile(null);
        setVideoOutUrl(' ')
        setVideoInFile(myFileVideoIn)
        setVideoInUrl(URL.createObjectURL(myFileVideoIn));

        console.log("imageInput: ", myFileVideoIn);
        // console.log("imageInput: ", URL.createObjectURL(myFileImgIn));
    }

    const onSendfileClick = async(event) => {
        event.preventDefault();

        if(!videoInFile){
            console.log("Please select image");
            return;
        }
        
        const myFileVideoOut = videoInFile;
        try{
            let formData = new FormData();            
            formData.append("image", myFileVideoOut);
            formData.append("text", "Hello, world");

            const response = await axios.post("http://localhost:5000/testPostDetector", formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            // console.log(response.data.text[1].main_text);

            const detail = response.data.objectInfo

            console.log(response.data.state);
            console.log(detail);
            
            const imageUrl = `data:image/jpeg;base64,${response.data.imagePath}`;
            const fileName = videoInFile.name;

            const outputFile = new File([imageUrl], fileName, { type: "image/jpeg" });
            console.log("imageOutput: ",outputFile);

            setVideoOutFile(outputFile);
            setVideoOutUrl(imageUrl);
            setDetailOut(detail);
        }catch(error){
            console.log(error);
        }
    }

    function onClearClick(){
        setVideoInUrl(' ');
        setVideoOutUrl(' ');
        setVideoInFile(null);
        setVideoOutFile(null);
    }

  return (
    <div className="Detection-contrainer">
        <div className='Detection-header'>
            <h2>VIDEO DETECTION PAGE</h2>
        </div>
        <div className="Detection-content">
            <VideoInput vvideInUrl={vidInUrl} onUploadfileClick={onUploadfileClick} ref={videoRef} />
            <DetectionOutput videoOutFile={vidOutFile} imgOutUrl={videoOutUrl} detailOut={detailOut} />
        </div>
        <div className="Detection-button">
            <button className="Detection-button-submit" onClick={onSendfileClick}>Submit</button>
            <button className="Detection-button-clear" onClick={onClearClick}>Clear</button>
        </div>
    </div>
  )
}

export default VideoPage
