import React, { useRef, useState } from "react";
import axios from 'axios';
import './VideoPage.css';
import VideoInput from "./VideoInput";
import VideoOutput from "./VideoOutput";

function VideoPage() {
    const vidRef =useRef(null);
    const [vidInUrl, setVidInUrl] =  useState(' ');
    const [vidInFile, setVidInFile] =  useState(null);
    const [vidOutUrl, setVidOutUrl] =  useState(' ');
    const [vidOutFile, setVidOutFile] =  useState(null);
    const [detailOut, setDetailOut] =  useState(null);

    function onUploadfileClick(){
        const myFileVidIn = vidRef.current.files[0];
        setVidOutFile(null);
        setVidOutUrl(' ')
        setVidInFile(myFileVidIn)

        if(!myFileVidIn){
            console.log("Please select image");
            return;
        }
        setVidInUrl(URL.createObjectURL(myFileVidIn));
        console.log("imageInput: ", myFileVidIn);
        // console.log("imageInput: ", URL.createObjectURL(myFileImgIn));
    }

    const onSendfileClick = async(event) => {
        event.preventDefault();

        if(!vidInFile){
            console.log("Please select image");
            return;
        }
        
        const myFileVideoOut = vidInFile;
        try{
            let formData = new FormData();            
            formData.append("video", myFileVideoOut);
            formData.append("text", "web-video");

            const response = await axios.post("http://localhost:5000/testPostVideo", formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });

            console.log(response.data.state);
            
            const outputUrl = `data:video/mp4;base64,${response.data.outputPath}`;
            const fileName = vidInFile.name;

            const outputFile = new File([outputUrl], fileName, { type: "video/mp4" });
            console.log("imageOutput: ",outputFile);

            // setVidOutFile(outputFile);
            // setVidOutUrl(imageUrl);
            // setDetailOut(detail);
        }catch(error){
            console.log(error);
        }
    }

    const onSendfileClickTest = async(event) => {
        event.preventDefault();
        if(!vidInFile){
            console.log("Please select image");
            return;
        }
        const myFileVideoOut = vidInFile;
        const myURLVideoOut = vidInUrl;
        setVidOutFile(myFileVideoOut);
        setVidOutUrl(myURLVideoOut);
    }

    function onClearClick(){
        setVidInUrl(' ');
        setVidOutUrl(' ');
        setVidInFile(null);
        setVidOutFile(null);
    }

  return (
    <div className="Video-contrainer">
        <div className='Video-header'>
            <h2>VIDEO DETECTION PAGE</h2>
        </div>
        <div className="Video-content">
            <VideoInput vidInUrl={vidInUrl} onUploadfileClick={onUploadfileClick} ref={vidRef} />
            <VideoOutput vidOutFile={vidOutFile} vidOutUrl={vidOutUrl} detailOut={detailOut} />{/* <DetectionOutput vidOutFile={vidOutFile} imgOutUrl={vidOutUrl} detailOut={detailOut} /> */}
        </div>
        <div className="Video-button">
            <button className="Video-button-submit" onClick={onSendfileClickTest}>Submit</button>
            <button className="Video-button-clear" onClick={onClearClick}>Clear</button>
        </div>
    </div>
  )
}

export default VideoPage
