import React, { useState } from "react";
import axios from 'axios';
import './HomePage.css';

function HomePage(){
    const [detailAcc, setDetailAcc] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    
    const onGetAccClick = async(event) => {
        event.preventDefault();
        setIsLoading(true);
        console.log("Get-Acc")

        try{
            let formData = new FormData();
            formData.append("text", "Hello, world");

            const response = await axios.post("http://localhost:5000/testGetAccuracy", formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });

            console.log(response.data.total);
            setDetailAcc(response.data);
        }catch(error){
            console.log(error);
        }
        setIsLoading(false)
    }
    function onClearClick(){
        setDetailAcc(null);
    }

    let accurate = null;
    if(detailAcc){
        accurate = 
        <div>
            <p>
                {Object.entries(detailAcc).map(([key, value]) => (
                    <p key={key}>{`${key}: ${value}`}</p>
                ))}
            </p>
        </div>
    }

