import React, { useState } from "react";
import PopupDetail from './PopupDetail';
import './VideoOutput.css'


function VideoOutput(probs) {
    const [popupOpen, setPopupOpen] = useState(false);
    const [nextVideo, setNextVideo] = useState(false);

    // let popup = null;
    // if(popupOpen){
    //     if(!probs.videoOutFile){
    //         console.log("Please select image");
    //         setPopupOpen(false)
    //         return;
    //     }
    //    popup = <PopupDetail vidOutUrl={probs.vidOutUrl} detailOut={probs.detailOut} onPopupClose={() => setPopupOpen(false)} />
    // }

    const onVideoSaveClick = () => {
        const a = document.createElement('a');
        if(nextVideo){
            a.href = probs.vidShapeOutUrl;
            a.download = "shape_detect_" + probs.vidShapeOutFile.name;
        }else{
            a.href = probs.vidTypeOutUrl;
            a.download = "type_detect_" + probs.vidTypwOutFile.name;
        }
        a.href = probs.vidTypeOutUrl;
        a.download = 'output_download.mp4';
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
    };

    function onNextClick(){
        if(nextVideo){
            setNextVideo(false)
        }else{
            setNextVideo(true)
        }
    }

    let vidOutput = null;
    let tag = null;
    if(nextVideo){
        if(probs.vidShapeOutUrl !== ' '){
            tag = <p>Shape Detection Video</p>
            vidOutput = <video controls src={probs.vidTypeOutUrl} alt="VideoShapeOut" />
        }
    }else{
        if(probs.vidTypeOutUrl !== ' '){
            tag = <p>Type Detection Video</p>
            vidOutput = <video controls src={probs.vidTypeOutUrl} alt="VideoTypeOut" />
        }
    }
    
  return (
    <div className="Video-content-output">
        <div>
            <p className="Video-content-output-video">
                {tag}
                {vidOutput}
            </p>
            <p>
                {/* <button className="Video-content-output-button" onClick={() => setPopupOpen(true)}>Detail</button> */}
                <button className="Video-content-output-button" onClick={onVideoSaveClick}>Save</button>
                <button className="Video-content-output-next" onClick={onNextClick}>Next</button>
            </p>
        </div>
        {/* <div>
            {popup}
        </div> */}
    </div>
  )
}

export default VideoOutput
