import React, { useState } from "react";
import axios from 'axios';
import './HomePage.css';

function HomePage(){
    const [detailAcc, setDetailAcc] =  useState(null);
    
    const onGetAccClick = async(event) => {
        event.preventDefault();
        console.log("Get-Acc")

        try{
            let formData = new FormData();
            formData.append("text", "Hello, world");

            const response = await axios.post("http://localhost:5000/testGetAccuracy", formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });

            console.log(response.data.total);
            setDetailAcc(response.data);
        }catch(error){
            console.log(error);
        }
    }

    let accurate = null;
    if(detailAcc){
        accurate = 
        <div>
            <p>
                {Object.entries(detailAcc).map(([key, value]) => (
                    <p key={key}>{`${key}: ${value}`}</p>
                ))}
            </p>
        </div>
    }

    
    return(
        <div className="Home-contrainer">
            <div className='Home-header'>
                <h2>HOME PAGE</h2>
            </div>
            <div className="Home-content">
                <p>
                    {accurate}
                </p>
            </div>
            <div className="Home-button">
                <button className="Home-button-submit" onClick={onGetAccClick}>Submit</button>
                <button className="Detection-button-clear" onClick={onClearClick}>Clear</button>
        </div>
        </div>
    )
}

export default HomePage
